#pragma once
#include "globals.h"
#include "FileIO.h"

inline std::string license_voidsprite_short = "";

inline std::string license_OFL = "";

inline std::string decode(std::string& l) {
    auto zlibBytes = base64ToBytes(l);
	auto decompressed = decompressZlibWithoutUncompressedSize(zlibBytes.data(), zlibBytes.size());
	return std::string((char*)decompressed.data(), decompressed.size());
}

inline std::string getVSPShortLicense() {
	return decode(license_voidsprite_short);
}	

inline std::string getFontLicense() {
	return decode(license_OFL);
}